import React, { useState, useEffect } from "react";
import axios from "axios";
import { Bar } from "react-chartjs-2";
import { startOfMonth, endOfMonth, getDaysInMonth } from "date-fns";

const MonthlyBarChart = () => {
  const [monthData, setMonthData] = useState({});
  const [email, setEmail] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      const storedEmail = localStorage.getItem("email");
      if (!storedEmail) {
        console.error("Email not found in local storage");
        return;
      }

      setEmail(storedEmail);

      const result = await axios.post(
        "https://www.zoelindev.com/timer/API/bar.php",
        {
          email: storedEmail,
        }
      );
      setMonthData(result.data.monthData);
    };

    fetchData();
  }, []);

  const monthDataLabels = Array.from(Array(getDaysInMonth(new Date())), (_, i) =>
    (i + 1).toString()
  );
  const monthDataValues = Array.from(Array(getDaysInMonth(new Date())), (_, i) => {
    const date = new Date();
    date.setDate(i + 1);
    const formattedDate = date.toISOString().split("T")[0];
    return monthData[formattedDate] || 0;
  });

  const monthChartData = {
    labels: monthDataLabels,
    datasets: [
      {
        label: "Month",
        data: monthDataValues,
        backgroundColor: [
          "rgba(255, 99, 132, 0.2)",
          "rgba(255, 159, 64, 0.2)",
          "rgba(255, 205, 86, 0.2)",
          "rgba(75, 192, 192, 0.2)",
          "rgba(54, 162, 235, 0.2)",
          "rgba(153, 102, 255, 0.2)",
          "rgba(201, 203, 207, 0.2)",
          ...Array.from(
            Array(getDaysInMonth(new Date()) - monthDataValues.length),
            () => "rgba(0,0,0,0)"
          ),
        ],
        borderColor: [
          "rgb(255, 99, 132)",
          "rgb(255, 159, 64)",
          "rgb(255, 205, 86)",
          "rgb(75, 192, 192)",
          "rgb(54, 162, 235)",
          "rgb(153, 102, 255)",
          "rgb(201, 203, 207)",
          ...Array.from(
            Array(getDaysInMonth(new Date()) - monthDataValues.length),
            () => "rgba(0,0,0,0)"
          ),
        ],
        borderWidth: 1,
      },
    ],
  };

  return (
    <div>
      <h2>Monthly Exercise Time</h2>
      <Bar data={monthChartData} />
    </div>
  );
};

export default MonthlyBarChart;
