import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import Header from "../components/Header";
import { Bar } from 'react-chartjs-2';

const Report = () => {
  const [totalTime, setTotalTime] = useState({
    today: "",
    week: "",
    month: "",
  });

  const [weeklyData, setWeeklyData] = useState([]);
  const [monthlyData, setMonthlyData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const email = localStorage.getItem("email");
        //console.log(email);

        if (!email) {
          console.error("Email not found in localStorage");
          return;
        }

        const response = await axios.post(
          "https://www.zoelindev.com/timer/API/total.php",
          { email }
        );

        if (response.data) {
          setTotalTime({
            today: response.data.todayTotal,
            week: response.data.weekTotal,
            month: response.data.monthTotal,
          });

          setWeeklyData(response.data.weeklyData);
          setMonthlyData(response.data.monthlyData);

          // Prepare the data for the bar chart
          const completeWeeklyData = daysOfWeek.map((day) => {
            const dataForDay = response.data.weeklyData.find((data) => data.day === day);
            return dataForDay ? dataForDay : { day, total: 0 };
          });

          setWeeklyData(completeWeeklyData);

          console.log(response.data);
        }
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

   // Prepare the data for the bar chart
   const daysOfWeek = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];

   // Weekly chart data and options
  const weeklyChartData = {
    labels: weeklyData.map((data) => data.day),
    datasets: [
      {
        label: 'Weekly Exercise Time',
        data: weeklyData.map((data) => data.total),
        backgroundColor: 'rgba(75, 192, 192, 0.2)',
        borderColor: 'rgba(75, 192, 192, 1)',
        borderWidth: 1,
      },
    ],
  };

  const weeklyChartOptions = {
    scales: {
      y: {
        beginAtZero: true,
      },
    },
  };

  return (
    <div>
      <Header />
      <div>
        <h1></h1>
        <h2>Total Time</h2>
        <div>
          <p>Today</p>
          <p>{totalTime.today}</p>
          <p>MM:SS</p>
        </div>
        <div>
          <p>This week</p>
          <p>{totalTime.week}</p>
          <p>HH:MM:SS</p>
        </div>
        <div>
          <p>This month</p>
          <p>{totalTime.month}</p>
          <p>HH:MM:SS</p>
        </div>
      </div>

      <div>
        <h2>This week’s report</h2>
        <Bar data={weeklyChartData} options={weeklyChartOptions} />
      </div>

      <div>
        <h2>This month’s report</h2>
      </div>
    </div>
  );
};

export default Report;
