import React, { useState, useEffect } from "react";
import axios from "axios";
import { Bar } from "react-chartjs-2";
import { startOfMonth, endOfMonth, getDaysInMonth } from "date-fns";

const MonthlyBarChart = () => {
  const [monthData, setMonthData] = useState({});
  const [email, setEmail] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      const storedEmail = localStorage.getItem("email");
      if (!storedEmail) {
        console.error("Email not found in local storage");
        return;
      }

      setEmail(storedEmail);

      const result = await axios.post(
        "https://www.zoelindev.com/timer/API/bar.php",
        {
          email: storedEmail,
        }
      );
      setMonthData(result.data.monthData);
    };

    fetchData();
  }, []);

  const monthDataLabels = Array.from(Array(getDaysInMonth(new Date())), (_, i) =>
    (i + 1).toString()
  );
  const monthDataValues = Array.from(Array(getDaysInMonth(new Date())), (_, i) => {
    const date = new Date();
    date.setDate(i + 1);
    const formattedDate = date.toISOString().split("T")[0];
    return monthData[formattedDate] || 0;
  });

  const monthChartData = {
    labels: monthDataLabels,
    datasets: [
      {
        label: "Month",
        data: monthDataValues,
        backgroundColor: monthDataValues.map((_, i) =>
          `rgba(${Math.floor(Math.random() * 256)}, ${Math.floor(
            Math.random() * 256
          )}, ${Math.floor(Math.random() * 256)}, 0.2)`
        ),
        borderColor: monthDataValues.map((_, i) =>
          `rgb(${Math.floor(Math.random() * 256)}, ${Math.floor(
            Math.random() * 256
          )}, ${Math.floor(Math.random() * 256)})`
        ),
        borderWidth: 1,
      },
    ],
  };

  return (
    <div>
      <Bar data={monthChartData} />
    </div>
  );
};

export default MonthlyBarChart;
