import { useState, useEffect } from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faDumbbell, faCirclePlay, faCirclePause, faRotateLeft } from '@fortawesome/free-solid-svg-icons'

const Timer = () => {
    const [workTime, setWorkTime] = useState({ minutes: 0, seconds: 0 });
    const [restTime, setRestTime] = useState({ minutes: 0, seconds: 0 });
    const [sets, setSets] = useState(1);
    const [currentSet, setCurrentSet] = useState(1);
    const [isRunning, setIsRunning] = useState(false);
    const [isRest, setIsRest] = useState(false);

    useEffect(() => {
        if (!isRunning) return;
        const timerId = setInterval(() => {
          handleTick();
        }, 1000);
        return () => clearInterval(timerId);
      }, [isRunning, isRest, workTime, restTime]);



    const handleWorkTime = (event) => {
        const minutes = parseInt(event.target.value, 10);
        setWorkTime({ minutes, seconds: workTime.seconds });
    };

    const handleRestTime = (event) => {
        const minutes = parseInt(event.target.value, 10);
        setRestTime({ minutes, seconds: restTime.seconds });
    };


    const handleSets = (num) => {
        setSets(num);
    };

    const handleReset = () => {
        setIsRunning(false);
        setCurrentSet(1);
        setIsRest(false);
        setWorkTime({ minutes: parseInt(document.getElementById("work-minutes").value), seconds: parseInt(document.getElementById("work-seconds").value)});
        setRestTime({ minutes: parseInt(document.getElementById("rest-minutes").value), seconds: parseInt(document.getElementById("rest-seconds").value)});
      };


      const handleStart = () => {
        setIsRunning(true);
        setWorkTime({ minutes: workTime.minutes, seconds: workTime.seconds });
        setRestTime({ minutes: restTime.minutes, seconds: restTime.seconds });
      };


    const handlePause = () => {
        setIsRunning(false);
    };


    const handleTick = () => {
        if (isRest) {
          if (restTime.minutes === 0 && restTime.seconds === 0) {
            if (currentSet === sets) {
              setIsRunning(false);
            } else {
              setCurrentSet(currentSet + 1);
              setIsRest(false);
            }
          } else if (restTime.seconds === 0) {
            setRestTime({ minutes: restTime.minutes - 1, seconds: 59 });
          } else {
            setRestTime({ ...restTime, seconds: restTime.seconds - 1 });
          }
        } else {
          if (workTime.minutes === 0 && workTime.seconds === 0) {
            setIsRest(true);
            if (currentSet === sets) {
              setIsRunning(false);
            }
          } else if (workTime.seconds === 0) {
            setWorkTime({ minutes: workTime.minutes - 1, seconds: 59 });
          } else {
            setWorkTime({ ...workTime, seconds: workTime.seconds - 1 });
          }
        }
      };



    const formatTime = (timeObj) => {
        const { minutes, seconds } = timeObj;
        const formattedMinutes = minutes.toString().padStart(2, "0");
        const formattedSeconds = seconds.toString().padStart(2, "0");
        return `${formattedMinutes}:${formattedSeconds}`;
      };



    return (
        <div className="timerPage">
            <div className="timer">
                {/* 將時間格式化為 00:00 的形式 */}
                <div className="timeDisplay">{formatTime(isRest ? restTime : workTime)}</div>

                <FontAwesomeIcon icon={faDumbbell} />

                {/* 修正錯誤：顯示目前正在倒數的是 work 還是 rest */}
                <div className="current">{isRest ? "Rest" : "Work"}</div>
                <div className="setsDisplay">{`Set ${currentSet} of ${sets}`}</div> 

                <div className="controlButtons">
                    {isRunning ? (
                        <FontAwesomeIcon icon={faCirclePause} onClick={handlePause} />
                    ) : (
                        <FontAwesomeIcon icon={faCirclePlay} onClick={handleStart} />
                    )}
                    <FontAwesomeIcon icon={faRotateLeft} onClick={handleReset} />
                </div>
            </div>
            <div className="settings">
                <div className="workTime">
                    <label>Work</label>


                    <input type="number" min="0" max="60" 
                    value={workTime.minutes}
                    onChange={(e) => {
                        const time = parseInt(e.target.value, 10);
                        handleWorkTime(time, workTime.seconds);
                    }} 
                    disabled={isRunning}
                    />

                    <input type="number" min="0" max="59" 
                    value={workTime.seconds}
                    onChange={(e) => {
                        const time = parseInt(e.target.value, 10);
                        handleWorkTime(workTime.minutes, time);
                    }} 
                    disabled={isRunning}
                    />


                </div>
                <div className="restTime">
                    <label>Rest</label>


                    <input type="number" min="0" max="60"
                    value={restTime.minutes}
                    onChange={(e) => {
                        const time = parseInt(e.target.value, 10);
                        handleRestTime(time, restTime.seconds);
                    }} 
                    disabled={isRunning}
                    />

                    <input type="number" min="0" max="59" 
                    value={restTime.seconds}
                    onChange={(e) => {
                        const time = parseInt(e.target.value, 10);
                        handleRestTime(restTime.minutes, time);
                    }} 
                    disabled={isRunning}
                    />
                </div>
                <div className="sets">
                    <label>Sets</label>

                    <input type="number"
                        min="1"
                        max="20"
                        value={sets}
                        onChange={(e) => {
                            const num = parseInt(e.target.value, 10);
                            handleSets(num);
                        }} />
                </div>
            </div>
        </div>
    );
};

export default Timer;
