import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import Header from "../components/Header";
import { Bar } from "react-chartjs-2";

const Report = () => {
  const [totalTime, setTotalTime] = useState({
    today: "",
    week: "",
    month: "",
  });

  const [weeklyData, setWeeklyData] = useState([]);
  const [monthlyData, setMonthlyData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const email = localStorage.getItem("email");

        if (!email) {
          console.error("Email not found in localStorage");
          return;
        }

        const response = await axios.post(
          "https://www.zoelindev.com/timer/API/total.php",
          { email }
        );

        if (response.data) {
          setTotalTime({
            today: response.data.todayTotal,
            week: response.data.weekTotal,
            month: response.data.monthTotal,
          });

          setWeeklyData(response.data.weeklyData);
          setMonthlyData(response.data.monthlyData);

          console.log(response.data);
        }
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  const weeklyChartData = {
    labels: ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"],
    datasets: [
      {
        label: "Weekly Exercise Time",
        data: weeklyData,
        backgroundColor: [
          "rgba(255, 99, 132, 0.2)",
          "rgba(255, 159, 64, 0.2)",
          "rgba(255, 205, 86, 0.2)",
          "rgba(75, 192, 192, 0.2)",
          "rgba(54, 162, 235, 0.2)",
          "rgba(153, 102, 255, 0.2)",
          "rgba(201, 203, 207, 0.2)",
        ],
        borderColor: [
          "rgb(255, 99, 132)",
          "rgb(255, 159, 64)",
          "rgb(255, 205, 86)",
          "rgb(75, 192, 192)",
          "rgb(54, 162, 235)",
          "rgb(153, 102, 255)",
          "rgb(201, 203, 207)",
        ],
        borderWidth: 1,
      },
    ],
  };

//   const monthlyChartData = {
//     labels: monthlyData.map((_, index) => index + 1),
//     datasets: [
//       {
//         label: "Monthly Exercise Time",
//         data: monthlyData,
//         backgroundColor: "rgba(75, 192, 192, 0.2)",
//         borderColor: "rgba(75, 192, 192, 1)",
//         borderWidth: 1,
//       },
//     ],
//   };

  const chartOptions = {
    scales: {
      y: {
        beginAtZero: true,
      },
    },
  };

  return (
    <div>
      <Header />
      <div>
        <h2>Total Time</h2>
        <div>
          <p>Today</p>
          <p>{totalTime.today}</p>
          <p>{totalTime.today}</p>
          <p>MM:SS</p>
        </div>
        <div>
          <p>This week</p>
          <p>{totalTime.week}</p>
          <p>HH:MM:SS</p>
        </div>
        <div>
          <p>This month</p>
          <p>{totalTime.month}</p>
          <p>HH:MM:SS</p>
        </div>
      </div>

      <div>
        <h2>This week’s report</h2>
        <Bar data={weeklyChartData} options={chartOptions} />
      </div>

      <div>
        <h2>This month’s report</h2>
        <Bar data={monthlyChartData} options={chartOptions} />
      </div>
    </div>
  );
};

export default Report;
