import { useState, useEffect } from "react";
import axios from "axios";
import Header from "../components/Header";
import WeeklyBarChart from "../components/WeeklyBarChart"
import MonthlyBarChart from "../components/MonthlyBarChart"

const Report = () => {
  const [totalTime, setTotalTime] = useState({
    today: "",
    week: "",
    month: "",
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const email = localStorage.getItem("email");

        if (!email) {
          console.error("Email not found in localStorage");
          return;
        }

        const response = await axios.post(
          "https://www.zoelindev.com/timer/API/total.php",
          { email }
        );

        if (response.data) {
          setTotalTime({
            today: response.data.todayTotal,
            week: response.data.weekTotal,
            month: response.data.monthTotal,
          });

          console.log(response.data);
        }
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      <Header />
      <div>
        <h2>Total Time</h2>
        <div>
          <p>Today</p>
          <p>{totalTime.today}</p>
          <p>MM:SS</p>
        </div>
        <div>
          <p>This week</p>
          <p>{totalTime.week}</p>
          <p>HH:MM:SS</p>
        </div>
        <div>
          <p>This month</p>
          <p>{totalTime.month}</p>
          <p>HH:MM:SS</p>
        </div>
      </div>

      <h2>Weekly Exercise Time</h2>
      <p>X:mins Y:week of day</p>
      <WeeklyBarChart/>

      <h2>Monthly Exercise Time</h2>
      <MonthlyBarChart/>
    </div>
  );
};

export default Report;
